    addNewElement(type) {
      var elementId = this.elementsHighestId + 1;
      var elementData = {
        type: type,
        id: elementId,
        selected: false,
        moving: false,
        resizing: false,
        backgroundColor: this.elementsDefaultBackgroundColors[type]
      };
      this.elements[type][elementId] = elementData;
      this.elementsHighestId += 1;
      this.elementsCount += 1;
    },
